package de.rub.nds.tlsattacker.core.protocol.preparator;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertEquals;

import de.rub.nds.modifiablevariable.util.ArrayConverter;
import de.rub.nds.tlsattacker.core.certificate.CertificateKeyPair;
import de.rub.nds.tlsattacker.core.constants.CipherSuite;
import de.rub.nds.tlsattacker.core.constants.HandshakeMessageType;
import de.rub.nds.tlsattacker.core.constants.ProtocolVersion;
import de.rub.nds.tlsattacker.core.crypto.keys.CustomRSAPrivateKey;
import de.rub.nds.tlsattacker.core.protocol.message.CertificateMessage;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import org.bouncycastle.crypto.tls.Certificate;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.EnumSource;

public class CertificateMessagePreparatorTest
        extends AbstractProtocolMessagePreparatorTest<
                CertificateMessage, CertificateMessagePreparator> {

    private final Certificate certificate;

    public CertificateMessagePreparatorTest() throws IOException {
        super(CertificateMessage::new, CertificateMessagePreparator::new);
        context.setSelectedCipherSuite(CipherSuite.TLS_RSA_WITH_AES_128_CBC_SHA);
        certificate =
                Certificate.parse(
                        new ByteArrayInputStream(
                                ArrayConverter.hexStringToByteArray(
                                        "")));
    }

    /** Test of prepareHandshakeMessageContents method, of class CertificateMessagePreparator. */
    @Test
    @Override
    public void testPrepare() throws IOException {
        context.getConfig()
                .setDefaultExplicitCertificateKeyPair(
                        new CertificateKeyPair(
                                certificate,
                                new CustomRSAPrivateKey(
                                        context.getConfig().getDefaultClientRSAModulus(),
                                        context.getConfig().getDefaultClientRSAPrivateKey())));
        context.getConfig().setAutoSelectCertificate(false);
        preparator.prepare();
        assertArrayEquals(
                ArrayConverter.hexStringToByteArray(
                        ""),
                message.getCertificatesListBytes().getValue());
        assertEquals(0x000397, (int) message.getCertificatesListLength().getValue());
        assertEquals(
                HandshakeMessageType.CERTIFICATE.getValue(), (byte) message.getType().getValue());
        assertEquals(0x00039A, (int) message.getLength().getValue());
    }

    @ParameterizedTest
    @EnumSource(
            value = ProtocolVersion.class,
            names = {"SSL3", "TLS10", "TLS11", "TLS12"})
    public void testPrepareWithDifferentProtocolVersions(ProtocolVersion protocolVersion)
            throws IOException {
        context.setSelectedProtocolVersion(protocolVersion);
        testPrepare();
    }

    @ParameterizedTest
    @EnumSource(
            value = ProtocolVersion.class,
            names = {"SSL3", "TLS10", "TLS11", "TLS12"})
    public void testPrepareNoContextWithDifferentProtocolVersions(ProtocolVersion protocolVersion) {
        context.setSelectedProtocolVersion(protocolVersion);
        testPrepareNoContext();
    }
}
